DROP TABLE Users CASCADE CONSTRAINTS;
DROP TABLE Friendships CASCADE CONSTRAINTS;
DROP TABLE Groups CASCADE CONSTRAINTS;
DROP TABLE GroupMembers CASCADE CONSTRAINTS;
DROP TABLE Messages CASCADE CONSTRAINTS;
PURGE RECYCLEBIN;

CREATE TABLE Users(
  userID number(10) PRIMARY KEY,
  fname varchar2(32),
  lname varchar2(32),
  email varchar2(50),
  DOB timestamp,
  last_login timestamp);

CREATE TABLE Friendships(
  senderID number(10),
  recipientID number(10),
  states varchar2(32),
  friendship_date timestamp,
  constraint f_pk PRIMARY KEY (senderID, recipientID),
  constraint F_S_FK FOREIGN KEY (senderID) REFERENCES Users(userID),
  constraint F_R_FK FOREIGN KEY (recipientID) REFERENCES Users(userID));

CREATE TABLE Groups(
	groupID NUMBER(10) PRIMARY KEY,
	name VARCHAR2(32),
	description VARCHAR2(1024),
	memberLimit NUMBER(10));

CREATE TABLE GroupMembers(
	userID NUMBER(10), 
	groupID NUMBER(10), 
	PRIMARY KEY (userID, groupID),
	CONSTRAINT gm_fk_uid FOREIGN KEY (userID) REFERENCES Users(userID),
	CONSTRAINT gm_fk_gid FOREIGN KEY (groupID) REFERENCES Groups(groupID));

--Assumption : replyTo are all valid msgIDs.
CREATE TABLE Messages(
	msgID NUMBER(10) PRIMARY KEY, 
	senderID NUMBER(10), 
	recipientID NUMBER(10), 
	groupID NUMBER(10), 
	replyTo number(10),
	subject VARCHAR2(32),
	msgText VARCHAR2(100 CHAR),
	dateSent TIMESTAMP,
	CONSTRAINT m_fk_sid FOREIGN KEY (senderID) REFERENCES Users(userID),
	CONSTRAINT m_fk_rid FOREIGN KEY (recipientID) REFERENCES Users(userID),
	CONSTRAINT m_fk_gid FOREIGN KEY (groupID) REFERENCES Groups(groupID));   

