--Trigger for memberLimit of Groups when adding members to GroupMembers
CREATE OR REPLACE TRIGGER MemberLimitT
BEFORE INSERT ON GroupMembers
FOR EACH ROW
DECLARE memLimit number(10); count1 number(10);
BEGIN
	SELECT memberLimit INTO memLimit
	FROM Groups G 
	WHERE :NEW.groupID = G.groupID;
	
	SELECT COUNT(*) INTO count1
	FROM GroupMembers GM 
	WHERE :NEW.groupID = GM.groupID;
	
	IF (memLimit <= count1) THEN
		ROLLBACK;
	END IF;
END;
/

--Trigger for removing user from groups when deleted from FaceSpace
CREATE OR REPLACE TRIGGER DeleteUser
BEFORE DELETE on Users
FOR EACH ROW
DECLARE groupNum number(10); otherUser number(10); temp number(1);
Begin
	--delete from Group Members table
	DELETE FROM GroupMembers
	WHERE userID = :OLD.userID;
	
	-- delete from Frienship table
	DELETE FROM Friendships
	WHERE senderID = :OLD.userID
	OR recipientID = :OLD.userID;

	-- set userID to null in Messages table
	UPDATE Messages
	SET senderID = NULL
	WHERE senderID = :OLD.userID;
	
	UPDATE Messages
	SET recipientID = NULL
	WHERE recipientID = :OLD.userID;

	-- Delete messages if both users are deleted
	DELETE FROM Messages
	WHERE senderID IS NULL AND recipientID IS NULL;
		 	
END;
/


